// frontend/src/utils/validators.js
export const nameRegex = /^[A-Za-z\s]+$/;
export const idNumberRegex = /^[0-9]{13}$/;
export const accountRegex = /^[0-9]{6,20}$/;
export const passwordRegex = /^(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[!@#$%^&*]).{8,}$/;
