import { useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../services/api";
import './Login.css';
import Register from "./pages/Register";

export default function Login() {
  const [form, setForm] = useState({ accountNumber: "", password: "" });
  const [msg, setMsg] = useState("");
  const navigate = useNavigate();

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await api.post("/auth/login", form);
      navigate("/payment");
    } catch (err) {
      setMsg(err.response?.data?.message || "Error");
    }
  };

  return (
    <div className="wrapper">
      <form action="" onSubmit={handleSubmit}>
        <h1>Login</h1>
        <div className="input-box">
          <input name="accountNumber" placeholder="Account Number" onChange={handleChange} />
        </div>
        <div className="input-box">
          <input name="password" type="password" placeholder="Password" onChange={handleChange} />
        </div>

        <div className="remember-forget">
          <label><input type="checkbox"/>Remember Me</label>
          <a href="#">Forgot Password?</a>
        </div>

        <button type="submit">Login</button>

        <div className="register-link">
          <Router>
            <nav style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
              <Link to="/" style={{ margin: "0 10px" }}>Register</Link>
            </nav>

            <Routes>
              <Route path="/" element={<Register />} />
            </Routes>
          </Router>
          <p>Don't Have An Account? <a href="pages">Register</a></p>
          <Link to="/" style={{ margin: "0 10px" }}>Register</Link>
        </div>

      </form>
      <p>{msg}</p>
    </div>
  );
}
