export const nameRegex = /^[A-Za-z\s]+$/;
export const idNumberRegex = /^[0-9]{13}$/;
export const accountRegex = /^[0-9]{6,20}$/;
export const passwordRegex = /^(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[!@#$%^&*()_+\-=[\]{};':"\\|,.<>/?]).{8,}$/;
export const currencyRegex = /^[A-Z]{3}$/;
export const swiftRegex = /^[A-Z0-9]{8,11}$/;
