import { useState, useEffect } from "react";
import api from "../services/api";
import "./PaymentForm.css";

export default function PaymentForm() {
  const [form, setForm] = useState({
    amount: "",
    currency: "ZAR",
    payeeAccount: "",
    swiftCode: "",
  });
  const [msg, setMsg] = useState("");
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMsg("");

    try {
      const res = await api.post("/payments", form);
      setMsg("‚úÖ Payment created successfully!");
      setPayments([...payments, res.data.payment]);
      setForm({ amount: "", currency: "ZAR", payeeAccount: "", swiftCode: "" });
    } catch (err) {
      setMsg(`‚ùå ${err.response?.data?.message || "Payment failed."}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    api
      .get("/payments")
      .then((res) => setPayments(res.data))
      .catch(() => setMsg("‚ùå Failed to load payments."));
  }, []);

  return (
    <div className="payment-container">
      <form className="payment-form" onSubmit={handleSubmit}>
        <h1>Make a Payment</h1>
        <p className="subtitle">Complete the details below to send a payment</p>

        <input
          name="amount"
          type="number"
          placeholder="Amount"
          value={form.amount}
          onChange={handleChange}
          required
        />

        <input
          name="currency"
          placeholder="Currency"
          value={form.currency}
          onChange={handleChange}
          required
        />

        <input
          name="payeeAccount"
          placeholder="Payee Account"
          value={form.payeeAccount}
          onChange={handleChange}
          required
        />

        <input
          name="swiftCode"
          placeholder="SWIFT Code"
          value={form.swiftCode}
          onChange={handleChange}
          required
        />

        <button type="submit" disabled={loading}>
          {loading ? "Processing..." : "Pay Now"}
        </button>

        {msg && <p className="message">{msg}</p>}
      </form>

      <div className="payment-list">
        <h2>Your Payments</h2>
        {payments.length === 0 ? (
          <p className="no-payments">No payments yet.</p>
        ) : (
          <ul>
            {payments.map((p) => (
              <li key={p._id} className={`payment-item ${p.status}`}>
                <span>
                  üí∏ {p.amount} {p.currency}
                </span>
                <span>‚Üí {p.payeeAccount}</span>
                <span className="status">[{p.status}]</span>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
