import { useState } from "react";
import api from "../../services/api";

export default function AdminDashboard() {
  const [form, setForm] = useState({ username: "", password: "" });
  const [msg, setMsg] = useState("");
  const [payments, setPayments] = useState([]);
  const [loggedIn, setLoggedIn] = useState(false);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleLogin = async e => {
    e.preventDefault();
    try {
      await api.post("/admin/login", form);
      setLoggedIn(true);
      setMsg("Admin logged in");
    } catch (err) {
      setMsg("Login failed");
    }
  };

  const fetchPayments = async () => {
    const res = await api.get("/admin/payments");
    setPayments(res.data);
  };

  const verifyPayment = async id => {
    await api.post(`/admin/payments/${id}/verify`);
    fetchPayments();
  };

  if (!loggedIn) {
    return (
      <div>
        <h2>Admin Login</h2>
        <form onSubmit={handleLogin}>
          <input name="username" placeholder="Username" onChange={handleChange} />
          <input name="password" type="password" placeholder="Password" onChange={handleChange} />
          <button type="submit">Login</button>
        </form>
        <p>{msg}</p>
      </div>
    );
  }

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <button onClick={fetchPayments}>Refresh Payments</button>
      <ul>
        {payments.map(p => (
          <li key={p._id}>
            {p.amount} {p.currency} from {p.userId.fullName} â†’ {p.payeeAccount} [{p.status}]
            <button onClick={() => verifyPayment(p._id)}>Verify</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
