import { useState, useEffect } from "react";
import api from "../services/api";

export default function PaymentForm() {
  const [form, setForm] = useState({ amount: "", currency: "ZAR", payeeAccount: "", swiftCode: "" });
  const [msg, setMsg] = useState("");
  const [payments, setPayments] = useState([]);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await api.post("/payments", form);
      setMsg("Payment created!");
      setPayments([...payments, res.data.payment]);
    } catch (err) {
      setMsg(err.response?.data?.message || "Error");
    }
  };

  useEffect(() => {
    api.get("/payments").then(res => setPayments(res.data));
  }, []);

  return (
    <div>
      <h2>Make Payment</h2>
      <form onSubmit={handleSubmit}>
        <input name="amount" placeholder="Amount" onChange={handleChange} />
        <input name="currency" placeholder="Currency" defaultValue="ZAR" onChange={handleChange} />
        <input name="payeeAccount" placeholder="Payee Account" onChange={handleChange} />
        <input name="swiftCode" placeholder="SWIFT Code" onChange={handleChange} />
        <button type="submit">Pay Now</button>
      </form>
      <p>{msg}</p>
      <h3>Your Payments</h3>
      <ul>
        {payments.map(p => (
          <li key={p._id}>{p.amount} {p.currency} â†’ {p.payeeAccount} [{p.status}]</li>
        ))}
      </ul>
    </div>
  );
}
